package Loop_study;

public class Do_While_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//var init/start point
		//do {
		//code
		//increment/decrement
		//}
		//while(condition)
		int a = 1;
		do {
			System.out.println("Do While A = "+a);
			a++;
		}while (a<=10);
		
	}
}	


