package Pattern_1;

public class P_16 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int a=1;a<=5;a++) {
	for (int b=1;b<=5;b++) {
		if(b==5||a==5||a+b==6) {
			System.out.print("*");
		}else {
			System.out.print(" ");
		}
	}System.out.println();
	
}
	}

}
