package Pattern_1;

public class P_3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=5;
     for(int a=1;a<=5;a++) {
	 for(int w=1;w<=x;w++) {
		 System.out.print("*");
	 }System.out.println();
	  x--; 
 }int d=2;
     for (int b=1; b<=4;b++) {
    	 for(int y=1; y<=d;y++) {
    	 System.out.print("*");
    	 }System.out.println();
		d++;
     }
	}
	

}
