package Pattern_1;

public class p_13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int y=4;
		int x=5;
		
        for(int a=1; a<=5;a++) {
	       for(int b=1; b<=y;b++) {
	          System.out.print(" ");
          }
           for (int b=1;b<=x;b++) {
            System.out.print("*");
          }
          System.out.println();
          y--;
	}
          
	
	}

}
