package Prectice_java;

public class pattern_30 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=5;
for (int i=1;i<=5;i++) {
	for(int j=1;j<=5;j++) {
		System.out.print("*");
	}
	for (int j=1;j<=a;j++) {
		System.out.print(" ");
	}System.out.println();
	a++;
}

	}	

}
