package control_statements;

public class Switch_use {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Employee_Id = 101;
		switch (Employee_Id) {
		case 101:
		System.out.println("Name \t\t= Arjun Prashad");
		System.out.println("Department \t= IT");
		System.out.println("Mobile no \t=9975004100");
		break;
		default: System.out.println(" No Employee found");
		}

	}

}
